import React, { Component } from 'react';


class Wikipedia extends Component {

    constructor(props) {
      super(props)
    
      this.state = {first:'',
                    fdescription:'',
                    second:'',
                    sdescription:'',
                    third:'',
                    tdescription:'',
                    fourth:'',
                    fdescription:'',
                    fresult:''
         
      }
    }

    submit = ()=>{ 
                  const req = this.state.first
                  console.log(req)
               const url = 'https://en.wikipedia.org/w/api.php?action=opensearch&format=json&origin=*&search='+req+'&namespace=0%7C4&limit=5&formatversion=2';

               fetch(url)
               .then(res => res.json())
               .then(data => {
                   this.setState({ fresult: data[1][1],fdescription:data[2][1] ,first:''})
                  console.log(this.state.fresult)
                  
               });
                  }
                  change = (e)=>{ this.setState({first:e.target.value})}

    render() {
        return (
            <div>
                <label>Wikipedia</label><br/>
                <input
                        type="text"
                        placeholder="Search"
                        value={this.state.first}
                        onChange={this.change}
                        
                    /><input type='button' value='Submit' onClick={this.submit}/>
                   
                    <h2>{this.state.fresult}</h2>
                    {this.state.fdescription}
            </div>
        );
    }
}

export default Wikipedia;
